import React, { Component } from 'react'
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';
import PropTypes from 'prop-types';
// import image from '../../public/img'

export default class Product extends Component {
    state = {
        src: `{img[0].image}${new Date().getMilliseconds()}`,
        fallbackSrc: `https://www.bitrefill.com/buy/aurelia-india/?hl=en
        https://www.bitrefill.com/content/cn/b_rgb%3Af9b03c%2Cc_pad%2Ch_424%2Cw_700/v1571666565/aurelia.png`,
        loaded: false,
        error: false
    };

    onImageLoaded = () => {
        this.setState({ loaded: true });
    };

    onImageError = () => {
        this.setState({ error: true });
    };

    refreshImage = () => {
        this.setState({
        error: false,
        loaded: false,
        src: `{img[0].image}${new Date().getMilliseconds()}`
        });
    };

    brokenImage = () => {
        this.setState({
        error: false,
        loaded: false,
        src: `{img[000].image}${new Date().getMilliseconds()}`
        });
    };


    render() {
        const{id, title, img, price, inCart} = this.props.product;
        const { src, loaded, fallbackSrc, error } = this.state;
        let imgSrc = !error ? src : fallbackSrc;

        return (
            <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
                <div className="card">

                <ProductConsumer>
                    {(value)=> (
                        <div className="img-container" onClick={()=>value.handleDetail(id)}>
                        <Link to="/details">
                            <img src={img[0].image} a onLoad={this.onImageLoaded}
            onError={this.onImageError} className="card-img-top"/>
            {!loaded && (
            <div className="image-container-overlay">
            <h5>none</h5>
            </div>
        )}
                        </Link>
                        <button className="cart-btn" 
                            disabled={inCart ? true:false} 
                            onClick={()=> {
                                value.addToCart(id);
                                value.openModal(id);
                            }}
                        >
                            {inCart?(<p className="text-capitalize mb-0 disabled">inCart</p>):<i className="fas fa-cart-plus"></i>}
                        </button>
                    </div>
                    )}

                </ProductConsumer>

                {/* Card Footer */}
                <div className="card-footer d-flex justify-content-between">
                    <p className="align-self-center mb-0">
                        {title}
                    </p>
                    <h5 className="main-text font-italic mb-0">
                        <span className="mr-1">$</span>
                        {price}
                    </h5>
                </div>
                </div>
            </ProductWrapper>
        )
    }
}

Product.propTypes = {
    product: PropTypes.shape({
        id:PropTypes.number,
        img:PropTypes.array,
        title: PropTypes.string,
        price:PropTypes.number,
        inCart:PropTypes.bool
    }).isRequired
};

const ProductWrapper = styled.div`
.card {
    border-color:transparent;
    transition: all 1s linear;
}


.card-footer {
    background: transparent;
    border-color: transparent;
    transition: all 1s linear;
}

&:hover {
    .card {
        border: 0.04rem solid rgba(0,0,0,0.2);
        box-shadow: 2px 2px 5px 0px rgba(0,0,0,0.2);
    }

    .card-footer {
        background: rgba(247,247,247);
    }
}
.img-container {
    position: relative;
    overflow: hidden;
}

.card-img-top {
    transition: all 1s linear;
}

.img-container:hover .card-img-top {
    transform: scale(1.2);
}

.cart-btn {
    position: absolute;
    bottom: 0;
    right: 0;
    padding: 0.2rem 0.4rem;
    background: var(--lightColor);
    border: none;
    color: var(--mainWhite);
    font-size: 1.4rem;
    border-radius: 0.5rem 0 0 0;
    transform: translate(100%, 100%);
    transition: all 1s linear;
}

.img-container:hover .cart-btn {
    transform:translate(0,0);
}

.cart-btn:hover {
    color:var(--mainColor);
    cursor: pointer;
}
`
